// ZonePage.js
import './ZonePage.css'
import React from 'react';
import { useParams } from 'react-router-dom';

const ZonePage = () => {
    const { id } = useParams();
    const history = useHistory();
    const cams = [1, 2, 3, 4];

    const goToCamera = (camId) => {
      history.push(`/zone/${id}/camera/${camId}`);
    };

  return (
    <div className="zone-page">
      <h2>Zone {id}</h2>
      <div className="camera-grid">
      {cams.map((camId) => (
      <div
        key={camId}
        className="camera-feed"
        onClick={() => goToCamera(camId)}
      >
        Cam {camId}
      </div>
    ))}
        <div className="alert-box">
          <h3>Alert Box</h3>
          {/* Mock alerts */}
          <p>No PPE detected on Cam 2</p>
        </div>
      </div>
    </div>
  );
};

export default ZonePage;
