import React, { useEffect, useRef, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ZonePage.css';

// Mock JSON data structure for alert messages
const mockAlerts = [
  { cameraId: 1, message: 'No PPE detected on Cam 1' },
  // Add more alerts as needed
];

const CameraFeed = ({ camId }) => {
  const videoRef = useRef(null);

  useEffect(() => {
    const enableStream = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) videoRef.current.srcObject = stream;
      } catch (err) {
        console.error("Error accessing the device camera", err);
      }
    };

    enableStream();

    // Cleanup function to stop the video stream
    return () => {
      if (videoRef.current && videoRef.current.srcObject) {
        videoRef.current.srcObject.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  return <video ref={videoRef} autoPlay playsInline muted />;
};

const ZonePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const cams = [1, 2, 3, 4];
  const [alerts, setAlerts] = useState(mockAlerts);

  const goToCamera = (camId) => {
    navigate(`/zone/${id}/camera/${camId}`);
  };

  return (
    <div className="zone-page">
      <h1 className="zone-title">Zone {id}</h1>
      <div className="camera-container">
        <div className="camera-grid">
          {cams.map((camId) => (
            <div key={camId} className="camera-feed-container" onClick={() => goToCamera(camId)}>
              <CameraFeed camId={camId} />
              <div className="camera-label">Cam {camId}</div>
            </div>
          ))}
        </div>
        <div className="alert-box">
          <h2>Alert Box</h2>
          {alerts.map((alert, index) => (
            <p key={index}>{alert.message}</p>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ZonePage;
