import React, { useEffect, useRef, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ZonePage.css';

const videoSources = {
  1: '/Users/ankitsoni/Desktop/CSDProject/PPE application/src/A01_20240222113000.mp4',
  2: '/path/to/video2.mp4',
  3: '/path/to/video3.mp4',
  4: '/path/to/video4.mp4'
};

const CameraFeed = ({ camId }) => {
  const videoRef = useRef(null);

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.src = videoSources[camId];
      videoRef.current.load();
    }
  }, [camId]);

  return <video className="video" ref={videoRef} autoPlay loop playsInline muted />;
};

const ZonePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const cams = [1, 2, 3, 4]; // Assuming you have 4 camera feeds
  const [alerts, setAlerts] = useState([]);

  const goToCamera = (camId) => {
    navigate(`/zone/${id}/camera/${camId}`);
  };

  return (
    <div className="zone-page">
      <h1 className="zone-title">Zone {id}</h1>
      <div className="camera-container">
        <div className="camera-grid">
          {cams.map((camId) => (
            <div key={camId} className="camera-feed-container" onClick={() => goToCamera(camId)}>
              <CameraFeed camId={camId} />
              <label className="camera-label">Cam {camId}</label>
            </div>
          ))}
        </div>
        <div className="alert-box">
          <h2>Alert Box</h2>
          {alerts.map((alert, index) => (
            <p key={index}>{alert.message}</p>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ZonePage;
